% elastic constants rotation%C11=169.9; C12=122.6; C44=76.2; %Cu Mishin in GPa%C11=243.4; C12=145.0; C44=116.0; % Fe Mendelev in GPa%C11=516.0; C12=198.0; C44=161.0; % W MEAM in GPa%C11=101.4; C12=45.1; C44=27.5; % Mg5Al2Si4 - Ninive et al%C11=116.5; C12=43.3; C44=27.3; % Mg4Al3Si4 - Ninive et alC11=111.9; C12=61.1; C44=33.4; % Al - Ninive et al GGA-PBEC = cubic_elast_stiff(C11,C12,C44);e1=[1 0 0]; e2=[0 1 0]; e3=[0 0 1];e2p=[1 1 -2]; e3p=[1 1 1]; e1p=[-1 1 0];e1p=e1p/norm(e1p);e2p=e2p/norm(e2p);e3p=e3p/norm(e3p);Q = [dot(e1p,e1) dot(e1p,e2) dot(e1p,e3)     dot(e2p,e1) dot(e2p,e2) dot(e2p,e3)     dot(e3p,e1) dot(e3p,e2) dot(e3p,e3)];    Cp = rotate_tensor_4th (C, Q);disp(sprintf('Cp(1,1,1,1) = %g',Cp(1,1,1,1)));disp(sprintf('Cp(2,2,2,2) = %g',Cp(2,2,2,2)));disp(sprintf('Cp(3,3,3,3) = %g',Cp(3,3,3,3)));disp(sprintf('Cp(1,1,2,2) = %g',Cp(1,1,2,2)));%disp(sprintf('Cp(1,1,3,3) = %g',Cp(1,1,3,3)));%disp(sprintf('Cp(2,2,3,3) = %g',Cp(2,2,3,3)));disp(sprintf('Cp(2,3,2,3) = %g',Cp(2,3,2,3)));%disp(sprintf('Cp(3,1,3,1) = %g',Cp(3,1,3,1)));%disp(sprintf('Cp(1,2,1,2) = %g',Cp(1,3,1,3)));a = 4.032;mu = Cp(2,3,2,3);nu = 0.332;E = mu*2*(1-nu);B = -(1+nu)*(1-2*nu)/(E*(1-nu));disp(sprintf('mu = %g GPa, nu = %g, E = %g  GPa, B = %g  GPa^-1',mu,nu,E,B));% Convert to correct units and scale with Al unit lengthmu = Cp(2,3,2,3)*10^9*10^-10*10^-10*a^2;%nu = 0.3;lambda = Cp(1,1,2,2);nu = lambda/(2*(lambda+mu));E = mu*2*(1+nu);B = -(1+nu)*(1-2*nu)/(E*(1-nu));disp(sprintf('mu = %g N/�2*a2, nu = %g, E = %g N/�2*a2, B = %g (N/�2*a2)^-1',mu,nu,E,B));